    private void SEARCHActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SEARCHActionPerformed

        try {
            Class.forName("com.mysql.cj.jdbc.Driver");

            Connection con = DriverManager.getConnection(
                    "jdbc:mysql://localhost:3306/ams", "root", "admin");
            Statement st = con.createStatement();
            String sql = "select * from manageflight";
            PreparedStatement pst = con.prepareStatement(sql);
            ResultSet rs = pst.executeQuery();
            DefaultTableModel dt = (DefaultTableModel) jTable1.getModel();
            dt.setRowCount(0);

            while (rs.next()) {
                Object o[] = {rs.getString("flightcode"), rs.getString("source"), rs.getString("destination"), rs.getString("takeoff"), rs.getInt("noofseats")};
                dt.addRow(o);
            }

        } catch (Exception e) {
            e.printStackTrace();
        }

    }//GEN-LAST:event_SEARCHActionPerformed

    private void INSERTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_INSERTActionPerformed

        try {
            Class.forName("com.mysql.cj.jdbc.Driver");

            Connection con = DriverManager.getConnection(
                    "jdbc:mysql://localhost:3306/ams", "root", "admin");

            String query = "insert into manageflight values (?,?,?,?,?)";
            PreparedStatement pst = con.prepareStatement(query);
            pst.setString(1, fc.getText());
            pst.setString(2, s.getText());
            pst.setString(3, d.getText());
            pst.setString(4, tof.getText());
            pst.setString(5, nos.getValue().toString());

            pst.executeUpdate();
            JOptionPane.showMessageDialog(this, "Data Inserted Successfully");

            con.close();

        } catch (Exception e) {
            e.printStackTrace();
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_INSERTActionPerformed

    private void DELETEActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DELETEActionPerformed

        try {
            Class.forName("com.mysql.cj.jdbc.Driver");

            Connection con = DriverManager.getConnection(
                    "jdbc:mysql://localhost:3306/ams", "root", "admin");
            Statement st = con.createStatement();
            String id = fc.getText();
            String sql = "DELETE FROM `manageflight` WHERE `flightcode` ='" + id + "' ";
            PreparedStatement pst = con.prepareStatement(sql);
            pst.executeUpdate();
            fc.setText("");
            s.setText("");
            d.setText("");
            tof.setText("");
            nos.setValue(1);
            
            JOptionPane.showMessageDialog(this, "Data Deleted Successfully!!");
            con.close();
            

        } catch (Exception e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
        }


    }//GEN-LAST:event_DELETEActionPerformed

    private void UPDATEActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_UPDATEActionPerformed

        try {
            String id = fc.getText();
            String source = s.getText();
            String des = d.getText();
            String tak = tof.getText();
            String noofseat = nos.getValue().toString();

            Class.forName("com.mysql.cj.jdbc.Driver");

            Connection con = DriverManager.getConnection(
                    "jdbc:mysql://localhost:3306/ams", "root", "admin");
            String flightcode = fc.getText();
            String sql = "UPDATE `manageflight` SET `source`=' " + source + " ', `destination` = '" + des + "', `takeoff` = '" + tak + "', `noofseats` = '" + noofseat + "' WHERE flightcode='" + flightcode + "' ";
            PreparedStatement pst = con.prepareStatement(sql);
            pst.execute();
            JOptionPane.showMessageDialog(this, "Record Updated!!");
            con.close();
        } catch (Exception e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
        }

    }//GEN-LAST:event_UPDATEActionPerformed

