    private void autogenerateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_autogenerateActionPerformed
        
         String alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
                    Random r = new Random();
                    char c1 = alphabet.charAt(r.nextInt(alphabet.length()));
                    char c2 = alphabet.charAt(r.nextInt(alphabet.length()));
                    int rand_int1 = r.nextInt(10);
                    int rand_int2 = r.nextInt(10);
                    int rand_int3 = r.nextInt(10);
                    int rand_int4 = r.nextInt(10);
                    String randomString = String.valueOf(c1) + c2 + rand_int1 + rand_int2 + rand_int3 + rand_int4;
                    fc.setText(randomString);   
        
        
    }//GEN-LAST:event_autogenerateActionPerformed

    private void fcFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_fcFocusLost

        try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            Scanner scanner = new Scanner(System.in);
            Connection con = DriverManager.getConnection(
                "jdbc:mysql://localhost:3306/ams", "root", "admin");
            String enteredfc = fc.getText();
            String sql = "SELECT * FROM manageflight WHERE flightcode = '"+enteredfc+"' " ;
            PreparedStatement pst = con.prepareStatement(sql);
            ResultSet rs = pst.executeQuery();

            if(rs.next()){
                s.setText(rs.getString("source"));
                d.setText(rs.getString("destination"));
                tof.setText(rs.getString("takeoff"));
                nos.setValue(rs.getInt("noofseats"));
            }
            con.close();
        } catch (Exception e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
        }

    }//GEN-LAST:event_fcFocusLost

